import { Component } from '@angular/core';
import {
  AbstractControl,
  FormControl,
  FormGroup,
  ValidatorFn,
  Validators,
} from '@angular/forms';
import { Router } from '@angular/router';
import { NotesService } from '../services/notes.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-add-note',
  templateUrl: './add-note.component.html',
  styleUrls: ['./add-note.component.css'],
})
export class AddNoteComponent {
  Note = new FormGroup({
    note: new FormControl('', [
      Validators.required,
      this.maxWordsValidator(500),
      this.specialCharactersValidator(),
    ]),
  });

  get note() {
    return this.Note.get('note');
  }

  constructor(private notesService: NotesService, private router: Router) {}

  ngOnInit(): void {}
  r: any;
  error = false;

  maxWordsValidator(maxWords: number): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const words = control.value ? control.value.trim().split(/\s+/) : [];
      return words.length > maxWords ? { maxWords: true } : null;
    };
  }
  specialCharactersValidator(): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const specialCharsPattern = /^[a-zA-Z0-9@;&*+-,\-\s]+$/;
      const containsSpecialChars = !specialCharsPattern.test(control.value);
      return containsSpecialChars ? { invalidSpecialChars: true } : null;
    };
  }

  add() {
    let note: any = this.Note.value;
    console.log(note);
    note.userId = localStorage.getItem('id');
    console.log(note);
    let now = new Date().toLocaleString();
    //console.log(now.toLocaleDateString() + ' ' + now.toLocaleTimeString());
    // note.creationTime = now.toLocaleDateString() + now.toLocaleTimeString();
    //note.creationTime = now;
    this.notesService.addNote(note).subscribe(async (result) => {
      this.r = result;
      console.log(result);
      if (result == null) {
        this.error = true;
      } else {
        // if (this.r.password == this.Note.value.password) {
        //this.notesService.generateToken(this.Note.value).subscribe(
        //async (response: any) => {
        // this.notesService.NoteUser(response.token);
        // localStorage.setItem('logged', 'true');
        await Swal.fire({
          title: "<h5 style='color:green;font-size:35px'>Note Added!</h5>",
          timer: 1000,
          showConfirmButton: false,
          icon: 'success',
        });

        this.router.navigate(['home']);
        //};
        // (error) => {
        //   this.error = true;
        // }
        // );
        // } else {
        //   await Swal.fire({
        //     title:
        //       "<h5 style='color:red;font-size:30px'>Wrong Credentials!!!</h5>",
        //     icon: 'error',
        //   });
        //   this.error = true;
        // }
      }
    });
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>Add Note</title>
    <link
      href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css"
      rel="stylesheet"
      integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC"
      crossorigin="anonymous"
    />
  </head>
  <body>
    <app-navbar></app-navbar>
    <div class="form-bg">
      <div class="container">
        <div class="row">
          <div class="fixed-header">
            <div class="form-container">
              <form
                [formGroup]="Note"
                (ngSubmit)="add()"
                class="form-horizontal"
              >
                <div class="row mt-5">
                  <div class="col offset-5">
                    <h3 class="title">Add a Note</h3>
                  </div>
                </div>
                <div class="form-group">
                  <textarea
                    class="form-control"
                    type="textarea"
                    placeholder="Add a note"
                    id="note"
                    name="note"
                    formControlName="note"
                    rows="10"
                    cols="50"
                  >
                  </textarea>
                </div>
                <h6 style="color: red" *ngIf="note?.touched">
                  Note is required
                </h6>
                <h6 style="color: red">
                  Only Special Characters allowed are [@ , ; & * + -]
                </h6>

                <div class="row">
                  <div class="col-1 offset-1">
                    <button class="btn btn-success" [disabled]="Note.invalid">
                      Add
                    </button>
                  </div>
                  <div class="col-1">
                    <button class="btn btn-danger">
                      <a
                        href="home"
                        style="text-decoration: none"
                        class="text-light"
                        >Cancel</a
                      >
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  </body>
</html>
