import { Component } from '@angular/core';
import { NotesService } from '../services/notes.service';
import { ActivatedRoute, Router } from '@angular/router';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent {
  constructor(
    private notesService: NotesService,
    private router: Router,
    private route: ActivatedRoute
  ) {}
  allNotes: any = [];
  id: any = 0;
  ngOnInit(): void {
    //let id = this.route.snapshot.params['id'];
    // this.route.queryParams.subscribe((params) => {
    //   this.id = params['id'];
    // });
    this.id = localStorage.getItem('id');
    this.notesService.getAllNotes(this.id).subscribe((result) => {
      this.allNotes = result;
      console.log(this.allNotes);
    });
  }
  delete(id: any) {
    this.notesService.deleteNote(id).subscribe((result) => {
      console.log(result);
      window.location.reload();
    });
  }
  logout() {
    localStorage.removeItem('id');
    this.router.navigate(['/']);
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>All Notes</title>
    <link
      href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css"
      rel="stylesheet"
      integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC"
      crossorigin="anonymous"
    />
  </head>
  <body>
    <app-navbar></app-navbar>
    <div>
      <button>
        <a routerLink="/addnote">Add a Note</a>
      </button>
    </div>
    <div style="text-align: right">
      <button type="button" (click)="logout()" class="btn btn-secondary">
        Logout
      </button>
    </div>
    <div class="container">
      <div>
        <table class="table table-striped">
          <thead class="text-primary">
            <th>Note Id</th>
            <th>Note</th>
            <th>Creation Time</th>
            <th>Delete Note</th>
          </thead>
          <tbody *ngFor="let note of allNotes">
            <tr>
              <td>{{ note.noteId }}</td>
              <td>{{ note.note }}</td>
              <td>{{ note.creationTime }}</td>
              <td>
                <button
                  type="button"
                  (click)="delete(note.noteId)"
                  class="btn btn-danger"
                >
                  Delete
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div></div>
    </div>
    <!-- <div class="card mt-5" style="width: 220px" *ngFor="let note of allNotes">
      <div class="card-body text-center mx-auto">
        <div class="cvp">
          <h3 class="card-title font-weight-bold">
            {{ note.noteId }}
          </h3>
          <br />
          <h5>
            <span style="color: cornflowerblue">Note: </span>{{ note.note }}
          </h5>
          <br />
        </div>
      </div> 
    </div>-->
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/js/bootstrap.bundle.min.js"></script>
  </body>
</html>
