import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
// import { ProductService } from '../services/product.service';
import { ActivatedRoute, Router, RouterModule, Routes } from '@angular/router';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: ['./index.component.sass'],
})
export class IndexComponent {
  Homepage = new FormGroup({
    search: new FormControl('', Validators.required),
  });

  get search() {
    return this.Homepage.get('search');
  }
  products: any = [];
  searchText: any;
  constructor(
    private routers: ActivatedRoute,
    //private productService: ProductService,
    private router: Router
  ) {}

  ngOnInit(): void {
    // this.productService.getProducts().subscribe((result) => {
    //   this.products = result;
    // });
  }

  Search() {
    this.searchText = this.Homepage.value.search;
    if (this.searchText !== '')
      this.router.navigate(['/results'], {
        queryParams: { search: this.Homepage.value.search },
      });
  }
}
<html>
  <head>
    <title>Product Management System</title>
    <link
      rel="stylesheet"
      href="https://cdn.jsdelivr.net/npm/bootstrap@4.0.0/dist/css/bootstrap.min.css"
      integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
      crossorigin="anonymous"
    />
  </head>
  <body style="text-align: center">
    <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
      <div class="container-fluid">
        <a class="navbar-brand" href="/">Shopping App</a>
        <button
          class="navbar-toggler border-0"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#menu"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="menu">
          <form class="d-flex ml-4 ms-auto" (ngSubmit)="Search()">
            <input
              class="form-control mr-2"
              style="width: 400px"
              type="search"
              placeholder="Search Here"
              aria-label="Search"
              formControlName="search"
            />

            <button class="btn ml-3" type="submit">Search</button>
          </form>
          <div style="display: flex; margin-left: 280px">
            <a
              type="button"
              class="btn btn-primary mr-4"
              href="/login"
              style="font-size: 20px"
              >Login</a
            >
            <a
              type="button"
              class="btn btn-primary"
              href="/registration"
              style="font-size: 20px"
              >Register</a
            >
          </div>
        </div>
      </div>
    </nav>
    <div class="container">
      <div class="row d-flex justify-content-center">
        <div
          class="card mt-5"
          style="width: 220px"
          *ngFor="let product of products"
        >
          <img
            class="card-img-top"
            src="{{ product.image }}"
            width="200px"
            height="200px"
          />
          <div class="card-body text-center mx-auto">
            <div class="cvp">
              <h3 class="card-title font-weight-bold">
                {{ product.name }}
              </h3>
              <br />
              <h5>
                <span style="color: cornflowerblue">Brand: </span
                >{{ product.brand }}
              </h5>
              <br />
              <input type="button" value="View Details" disabled="true" />
            </div>
          </div>
        </div>
      </div>
    </div>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
  </body>
</html>
