import { Component } from '@angular/core';
import { NotesService } from '../services/notes.service';
import { Router } from '@angular/router';
import { RouterModule, Routes } from '@angular/router';
import { FormControl, FormGroup, Validators } from '@angular/forms';
@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css'],
})
export class RegistrationComponent {
  Registration = new FormGroup({
    username: new FormControl('', [Validators.email, Validators.required]),
    password: new FormControl('', [Validators.required]),
    confirmPassword: new FormControl('', [Validators.required]),
  });
  error = false;

  get username() {
    return this.Registration.get('username');
  }
  get password() {
    return this.Registration.get('password');
  }
  get confirmPassword() {
    return this.Registration.get('confirmPassword');
  }

  constructor(private noteService: NotesService, private router: Router) {}

  ngOnInit(): void {}
  r: any;
  msg: any;
  register() {
    if (this.password?.value != this.confirmPassword?.value) {
      this.error = true;
      this.msg = 'Password and Confirm Password do not match!!!';
      return;
    }

    this.noteService
      .getlogin(this.Registration.value.username)
      .subscribe((result) => {
        this.r = result;

        if (this.r == null) {
          this.noteService
            .postregister(this.Registration.value)
            .subscribe(async (result) => {
              this.r = result;
              // await Swal.fire({
              //   title:
              //     "<h5 style='color:green;font-size:30px'>User Registered Successfully!</h5>",
              //   timer: 1000,
              //   showConfirmButton: false,
              //   icon: 'success',
              // });
              this.router.navigate(['login']);
            });
        } else {
          this.error = true;
          this.msg = 'User Already Exists!!!';
        }
      });
  }
}
<html>
  <head>
    <title>Product Management System</title>
    <link
      rel="stylesheet"
      href="https://cdn.jsdelivr.net/npm/bootstrap@4.0.0/dist/css/bootstrap.min.css"
      integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
      crossorigin="anonymous"
    />
  </head>
  <body>
    <!-- <app-navbar></app-navbar> -->
    <div class="form-bg">
      <div class="container-fluid">
        <div class="row mt-4" style="text-align: center">
          <div class="col">
            <p class="title">Register with your email and password:</p>
          </div>
        </div>
        <div class="form-container">
          <label *ngIf="error" class="error">{{ msg }}</label>
          <form
            [formGroup]="Registration"
            (ngSubmit)="register()"
            class="form-horizontal"
          >
            <div class="row">
              <div class="col">
                <div class="form-group">
                  <input
                    class="form-control inp p-4"
                    style="background-color: #dcdcdc; font-weight: bolder"
                    type="email"
                    id="email"
                    name="email"
                    placeholder="Email *"
                    formControlName="username"
                    email="true"
                  />
                  <span
                    class="error"
                    *ngIf="username && username.touched && username.invalid"
                    >Invalid Email</span
                  >
                </div>
                <br />
              </div>
            </div>
            <div class="row">
              <div class="col-6">
                <div class="form-group">
                  <input
                    class="form-control p-4"
                    type="password"
                    id="password"
                    placeholder="Password *"
                    style="background-color: #dcdcdc; font-weight: bolder"
                    name="password"
                    formControlName="password"
                    autocomplete="on"
                  />
                </div>
              </div>
              <div class="col-6">
                <div class="form-group">
                  <input
                    class="form-control p-4"
                    type="password"
                    placeholder="Confirm Password *"
                    id="confirmPassword"
                    style="background-color: #dcdcdc; font-weight: bolder"
                    name="confirmPassword"
                    formControlName="confirmPassword"
                    autocomplete="on"
                  />
                </div>
              </div>
            </div>
            <button class="btn mt-5" [disabled]="Registration.invalid">
              SUBMIT
            </button>
            <br />
            <span class="user-login">
              <a href="login">Have an account already?</a></span
            >
          </form>
        </div>
      </div>
    </div>
  </body>
</html>
