import { Component } from '@angular/core';
import {
  AbstractControl,
  FormControl,
  FormGroup,
  ValidatorFn,
  Validators,
} from '@angular/forms';
import { Router } from '@angular/router';
import { NotesService } from '../services/notes.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-add-note',
  templateUrl: './add-note.component.html',
  styleUrls: ['./add-note.component.css'],
})
export class AddNoteComponent {
  Note = new FormGroup({
    note: new FormControl('', [
      Validators.required,
      this.maxWordsValidator(500),
    ]),
  });
  error = false;

  get note() {
    return this.Note.get('note');
  }

  constructor(private notesService: NotesService, private router: Router) {}

  ngOnInit(): void {}
  r: any;

  maxWordsValidator(maxWords: number): ValidatorFn {
    return (control: AbstractControl): { [key: string]: any } | null => {
      const words = control.value ? control.value.trim().split(/\s+/) : [];
      return words.length > maxWords ? { maxWords: true } : null;
    };
  }
  specialCharactersValidator(): ValidatorFn {   return (control: AbstractControl): { [key: string]: any } | null => {     const specialCharsPattern = /[!@#$%^&*()_+{}\[\]:;<>,.?~\\/-]/;     const containsSpecialChars = specialCharsPattern.test(control.value);     return containsSpecialChars ? { specialChars: true } : null;   }; }}
  add() {
    let note: any = this.Note.value;
    console.log(note);
    note.userId = localStorage.getItem('id');
    console.log(note);
    let now = new Date().toLocaleString();
    //console.log(now.toLocaleDateString() + ' ' + now.toLocaleTimeString());
    // note.creationTime = now.toLocaleDateString() + now.toLocaleTimeString();
    //note.creationTime = now;
    this.notesService.addNote(note).subscribe(async (result) => {
      this.r = result;
      console.log(result);
      if (result == null) {
        this.error = true;
      } else {
        // if (this.r.password == this.Note.value.password) {
        //this.notesService.generateToken(this.Note.value).subscribe(
        //async (response: any) => {
        // this.notesService.NoteUser(response.token);
        // localStorage.setItem('logged', 'true');
        await Swal.fire({
          title: "<h5 style='color:green;font-size:35px'>Note Added!</h5>",
          timer: 1000,
          showConfirmButton: false,
          icon: 'success',
        });

        this.router.navigate(['home']);
        //};
        // (error) => {
        //   this.error = true;
        // }
        // );
        // } else {
        //   await Swal.fire({
        //     title:
        //       "<h5 style='color:red;font-size:30px'>Wrong Credentials!!!</h5>",
        //     icon: 'error',
        //   });
        //   this.error = true;
        // }
      }
    });
  }
}
<div class="form-bg">
  <div class="container">
    <div class="row">
      <div class="fixed-header">
        <div class="form-container">
          <form [formGroup]="Note" (ngSubmit)="add()" class="form-horizontal">
            <h3 class="title">Add a Note</h3>
            <div class="form-group">
              <textarea
                class="form-control"
                type="textarea"
                placeholder="Add a note"
                id="note"
                name="note"
                formControlName="note"
                rows="8"
                cols="50"
              >
              </textarea>
            </div>
            <span style="color: red" *ngIf="note?.invalid && note?.touched"
              >Note is required</span
            >
            <br />
            <button class="btn signin mt-2" [disabled]="Note.invalid">
              Add
            </button>
            <br />
            <a href="home" style="font-size: 14px">Back</a>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
