import { Component } from '@angular/core';
import { NotesService } from '../services/notes.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-allnotes',
  templateUrl: './allnotes.component.html',
  styleUrls: ['./allnotes.component.css'],
})
export class AllnotesComponent {
  constructor(private notesService: NotesService, private router: Router) {}
  allNotes: any = [];
  ngOnInit(): void {
    this.allNotes = this.notesService.getAllNotes(1).subscribe((result) => {
      this.allNotes = result;
    });
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <title>All Notes</title>
  </head>
  <body>
    <div class="card mt-5" style="width: 220px" *ngFor="let note of allNotes">
      <div class="card-body text-center mx-auto">
        <div class="cvp">
          <h3 class="card-title font-weight-bold">
            {{ note.noteId }}
          </h3>
          <br />
          <h5>
            <span style="color: cornflowerblue">Note: </span>{{ note.note }}
          </h5>
          <br />
        </div>
      </div>
    </div>
  </body>
</html>
