import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { NotesService } from '../services/notes.service';
import Swal from 'sweetalert2';
@Component({
  selector: 'app-add-note',
  templateUrl: './add-note.component.html',
  styleUrls: ['./add-note.component.css'],
})
export class AddNoteComponent {
  Note = new FormGroup({
    note: new FormControl('', Validators.required),
  });
  error = false;

  get note() {
    return this.Note.get('note');
  }

  constructor(private notesService: NotesService, private router: Router) {}

  ngOnInit(): void {}
  r: any;

  add() {
    this.notesService
      .addNote(this.Note.value.note)
      .subscribe(async (result) => {
        this.r = result;
        console.log(result);
        if (result == null) {
          this.error = true;
        } else {
          if (this.r.password == this.Login.value.password) {
            //this.notesService.generateToken(this.Login.value).subscribe(
            //async (response: any) => {
            // this.notesService.loginUser(response.token);
            // localStorage.setItem('logged', 'true');
            await Swal.fire({
              title:
                "<h5 style='color:green;font-size:35px'>Logging In...Please Wait !</h5>",
              timer: 1000,
              showConfirmButton: false,
              icon: 'success',
            });

            this.router.navigate(['home'], {
              queryParams: { id: this.r.userId },
            });
            //};
            // (error) => {
            //   this.error = true;
            // }
            // );
          } else {
            await Swal.fire({
              title:
                "<h5 style='color:red;font-size:30px'>Wrong Credentials!!!</h5>",
              icon: 'error',
            });
            this.error = true;
          }
        }
      });
  }
}
<div class="form-bg">
  <div class="container">
    <div class="row">
      <div class="fixed-header">
        <div class="form-container">
          <form [formGroup]="Note" (ngSubmit)="add()" class="form-horizontal">
            <h3 class="title">Add a Note</h3>
            <div class="form-group">
              <input
                class="form-control"
                type="text"
                placeholder="Add a note"
                id="note"
                name="note"
                formControlName="note"
              />
            </div>
            <span style="color: red" *ngIf="note?.invalid && note?.touched"
              >Note is required</span
            >
            <br />
            <button class="btn signin mt-2" [disabled]="Login.invalid">
              Log in
            </button>
            <br />
            <span class="register">
              Do Not Have an Account? Click Here to
              <a href="registration" style="font-size: 14px">Register</a>
            </span>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
