import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule, Routes } from '@angular/router';
import { NotesService } from '../services/notes.service';

@Component({
  selector: 'app-index',
  templateUrl: './index.component.html',
  styleUrls: [],
})
export class IndexComponent {
  // Homepage = new FormGroup({
  //   search: new FormControl('', Validators.required),
  // });

  // get search() {
  //   return this.Homepage.get('search');
  // }
  users: any = [];
  //searchText: any;
  constructor(
    private routers: ActivatedRoute,
    private notesService: NotesService,
    private router: Router
  ) {}

  ngOnInit(): void {}
  allUsers() {
    this.notesService.getProducts().subscribe((result) => {
      this.users = result;
    });
  }
  // Search() {
  //   this.searchText = this.Homepage.value.search;
  //   if (this.searchText !== '')
  //     this.router.navigate(['/results'], {
  //       queryParams: { search: this.Homepage.value.search },
  //     });
  // }
}
<html>
  <head>
    <title>Notes Management System</title>
    <link
      rel="stylesheet"
      href="https://cdn.jsdelivr.net/npm/bootstrap@4.0.0/dist/css/bootstrap.min.css"
      integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
      crossorigin="anonymous"
    />
  </head>
  <body>
    <app-navbar></app-navbar>
    <div class="container">
      <div class="row">
        <div class="col-3">
          <a
            type="button"
            class="btn btn-primary"
            href="/login"
            style="font-size: 20px"
            >Login</a
          >
        </div>
        <div class="col-6">
          <a
            type="button"
            class="btn btn-primary"
            href="/registration"
            style="font-size: 20px"
            >Register</a
          >
        </div>
      </div>
    </div>
    <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.4.1/js/bootstrap.min.js"></script>
  </body>
</html>
