import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  Login = new FormGroup({
    username: new FormControl('', Validators.required),
    password: new FormControl('', Validators.required),
  });
  error = false;

  get username() {
    return this.Login.get('username');
  }
  get password() {
    return this.Login.get('password');
  }

  constructor(private productService: ProductService, private router: Router) {}

  ngOnInit(): void {}
  r: any;

  login() {
    this.productService
      .getlogin(this.Login.value.username)
      .subscribe(async (result) => {
        this.r = result;
        if (result == null) {
          this.error = true;
        } else {
          if (this.r.password == this.Login.value.password) {
            this.productService.generateToken(this.Login.value).subscribe(
              async (response: any) => {
                this.productService.loginUser(response.token);
                localStorage.setItem('logged', 'true');
                await Swal.fire({
                  title:
                    "<h5 style='color:green;font-size:35px'>Logging In...Please Wait !</h5>",
                  timer: 1000,
                  showConfirmButton: false,
                  icon: 'success',
                });
                this.router.navigate(['home']);
              },
              (error) => {
                this.error = true;
              }
            );
          } else {
            await Swal.fire({
              title:
                "<h5 style='color:red;font-size:30px'>Wrong Credentials!!!</h5>",
              icon: 'error',
            });
            this.error = true;
          }
        }
      });
  }
}
